export default {
    name: "ConfirmDialog",
    props: {
        searchText: {
            type: String,
            required: true,
            default: ""
        },
        showSearch: {
            type: Boolean,
            required: true,
            default: false
        },
        resReady: {
            type: Boolean,
            required: true,
            default: false
        },
        searchResults: {
            type: Array,
            required: true,
            default() {}
        }
    },
    computed: {
        filteredResults() {
            return this.searchResults.filter(item => {
                return item.items && item.items.length > 0;
            });
        }
    },

    methods: {
        emitClose() {
            this.$emit("update:searchFocused", false);
        },
        markText(str) {
            if (str && this.searchText) {
                return str.replace(
                    new RegExp(`(${this.searchText})`, "i"),
                    `<span class='highlighted'>$1</span>`
                );
            }

            return str;
        }
    }
};
