import Navigation from "../components/Navigation";
import SearchResults from "../components/SearchResults";
let Search = null;

export default {
    name: "MainLayout",
    components: { Navigation, SearchResults },
    computed: {
        name() {
            return this.$store.state.auth.name;
        },
        showSearch() {
            // return false;
            return this.searchFocused && this.searchText.trim() !== "";
        }
    },
    beforeCreate() {
        Search = this.$RepositoryFactory.get("search");
    },
    mounted() {
        this.handleScroll();
        window.addEventListener("scroll", this.handleScroll);
    },
    destroyed() {
        window.removeEventListener("scroll", this.handleScroll);
    },
    meta: {
        title: "Home",
        titleTemplate: title => `${title} | LMC Admin Portal`,
        // meta tags
        meta: {
            description: {
                name: "description",
                content: "Content Management System for LMC"
            },
            keywords: { name: "keywords", content: "cms lmc admin portal" },
            equiv: {
                "http-equiv": "Content-Type",
                content: "text/html; charset=UTF-8"
            }
        }
    },

    data() {
        return {
            searchText: "",
            resReady: false,
            searchFocused: false,
            eventTimer: null,
            scrolled: false,
            drawer: false,
            showNavBtn: false,
            searchResults: [],
            navigationlinks: [
                {
                    title: "Dashboard",
                    icon: "dashboard",
                    link: "/dashboard"
                },
                {
                    title: "Customers",
                    icon: "account_circle",
                    link: "/accounts"
                },
                {
                    title: "Announcements",
                    icon: "announcement",
                    link: "/announcements"
                },
                {
                    title: "Product Categories",
                    icon: "category",
                    link: "/categories"
                },
                {
                    title: "Products",
                    icon: "cake",
                    link: "/products"
                },
                {
                    title: "Discounts",
                    icon: "money_off",
                    link: "/discounts"
                },
                {
                    title: "Comments",
                    icon: "comment",
                    link: "/comments"
                },
                {
                    title: "Orders",
                    icon: "store",
                    link: "/orders"
                },
                {
                    title: "Business Holidays",
                    icon: "date_range",
                    link: "/holidays"
                }
            ]
        };
    },

    methods: {
        handleScroll(e) {
            if (this.eventTimer) {
                window.clearTimeout(this.eventTimer);
            }
            this.eventTimer = window.setTimeout(() => {
                this.eventTimer = null;
                this.scrolled = window.scrollY >= 50 ? true : false;
            }, 100);
        },
        drawerLayoutChange(state) {
            this.showNavBtn = !state;
        },
        async search(evt) {
            this.searchFocused = true;
            this.resReady = false;
            if (this.searchText && this.searchText.trim() != "") {
                try {
                    const res = await Search.findItems(this.searchText);
                    this.searchResults = res.slice();
                    this.resReady = true;
                } catch (err) {}
            }
        },
        async logoutUser() {
            try {
                await this.$store.dispatch("auth/signout");
            } catch (err) {
            } finally {
                this.$router.push("/login").catch(err => {});
            }
        }
    }
};
