let Account = null;

export default {
    name: "AccountsIndex",
    mixins: [HelperMixin],
    meta() {
        return {
            title: "Customer Accounts",
            meta: {
                robots: { name: "robots", content: "noindex" }
            }
        };
    },
    beforeCreate() {
        Account = this.$RepositoryFactory.get("accounts");
    },
    created() {
        if (this.$route.query.type) {
            const itype = new RegExp(this.$route.query.type, "i");
            this.filter.forEach(el => {
                if (el.match(itype)) {
                    this.customerFilter = el;
                }
            });
        }
        if (this.$route.query.s) {
            this.search = this.$route.query.s;
        }
        if (this.$route.query.p && !isNaN(this.$route.query.p)) {
            this.pagination.page = Number.parseInt(this.$route.query.p);
        }
    },
    mounted() {
        this.onRequest({
            pagination: this.pagination,
            filter: undefined
        });
    },
    data() {
        return {
            customerList: [],
            search: "",
            searchReq: null,
            customerFilter: "All",
            filter: ["All", "Regular", "Reseller", "Partner"],
            loading: false,
            pagination: {
                sortBy: "joined",
                descending: true,
                page: 1,
                rowsPerPage: 9
            },
            columns: [
                {
                    name: "name",
                    field: "name",
                    label: "Name",
                    align: "left",
                    required: true,
                    sortable: true
                },
                {
                    name: "type",
                    field: "type",
                    align: "left",
                    label: "Type",
                    sortable: true
                },
                {
                    name: "joined",
                    field: "joined",
                    align: "left",
                    label: "Joined",
                    sortable: true
                },
                {
                    name: "login",
                    field: "login",
                    align: "left",
                    label: "Last Login",
                    sortable: true
                },
                {
                    name: "active",
                    field: "active",
                    align: "center",
                    label: "Status",
                    sortable: true
                },
                {
                    name: "action",
                    align: "left",
                    label: "Action"
                }
            ],
            data: [],
            original: []
        };
    },
    methods: {
        filterChanged(val) {
            const typeSel = this.$route.query.type;
            if (
                this.filter.includes(val) ||
                !typeSel ||
                !val.match(new RegExp(typeSel, "i"))
            ) {
                this.$router.replace({ query: { type: val } }).catch(err => {});
                this.search = "";
            }

            this.$refs["accntsTbl"].requestServerInteraction({
                pagination: { ...this.pagination, page: 1 }
            });
        },

        searchClear(evt) {
            let query = Object.assign({}, this.$route.query);
            delete query.s;
            this.$router.replace({ query }).catch(err => {});
            this.search = "";
        },

        searchInput(val) {
            let searchQry = Object.assign({}, this.$route.query, { s: val });
            if (!val) delete searchQry.s;
            this.$router
                .replace({
                    query: searchQry
                })
                .catch(err => {});
        },

        onChgPage(newPg) {
            let pageQry = Object.assign({}, this.$route.query, {
                p: newPg.page
            });
            if (!newPg) delete pageQry.p;

            this.$router
                .replace({
                    query: pageQry
                })
                .catch(err => {});
        },

        async onRequest(props) {
            this.loading = true;
            try {
                const resp = await Account.getAllAccounts(this.customerFilter);
                this.original = resp;
                this.data = this.original.slice();
                this.pagination.page = props.pagination.page;
            } catch (err) {
                this.showNotif(false, "Could not retrieve account details. ");
            } finally {
                this.loading = false;
            }
        }
    }
};

