import HelperMixin from "../../mixins/helpers";
let Discount = null,
    Product = null;

export default {
    name: "DiscountAdd",
    mixins: [HelperMixin],
    meta() {
        return {
            title: "Add Discount Rule"
        };
    },
    beforeCreate() {
        Discount = this.$RepositoryFactory.get("discounts");
        Product = this.$RepositoryFactory.get("products");
    },
    created() {
        this.discount.start = this.today.yyyymmdd + " 00:00";
        this.discount.end = this.today.yyyymmdd + " 23:59";
        if (process.env.CLIENT) this.getProducts();
    },
    data() {
        return {
            loading: false,
            hasSelect: true,
            targets: ["All", "Regular", "Reseller", "Partner"],
            options: null,
            productsList: [],
            discount: {
                value: 10,
                target: "All",
                products: null,
                start: "",
                end: ""
            }
        };
    },
    methods: {
        _isValidDatetime(val) {
            const dtpattern = /^\d{4}-(0?[1-9]|1[0-2])-(0?[1-9]|[12][0-9]|3[01]) (0?[0-9]|1[0-9]|2[0-3]):([0-9]|[0-5][0-9])$/g;
            return dtpattern.test(val) || "Invalid date & time format";
        },
        _isValidEndDt(val) {
            const end = new Date(val);
            const start = new Date(this.discount.start);
            return (
                end.getTime() > start.getTime() ||
                "End date needs to be greater"
            );
        },

        filterFn(val, update, abort) {
            if (!val || val.trim() == "") {
                abort();
                return;
            }

            update(() => {
                const needle = val.toLowerCase();
                this.options = this.productsList.filter(
                    v => v.label.toLowerCase().indexOf(needle) > -1
                );
            });
        },

        async getProducts() {
            Product.getProductSelection()
                .then(dat => {
                    this.productsList = dat;
                })
                .catch(err => {
                    this.productsList = [];
                });
        },

        onSubmit: async function(evt) {
            this.loading = true;
            try {
                await Discount.addDiscount(this.discount);
                this.showNotif(true, "Added new discount entry.");
                this.returnToPageIndex("/discounts");
            } catch (err) {
                this.showNotif(false, "Could not create discount entry. ");
            } finally {
                this.loading = false;
            }
        }
    }
};

