import HelperMixin from "../../mixins/helpers";
import ProductMixin from "../../mixins/product";
let Product = null,
    Category = null;

export default {
    name: "ProductAdd",
    mixins: [HelperMixin, ProductMixin],
    meta() {
        return {
            title: "Add Product"
        };
    },
    beforeCreate() {
        Category = this.$RepositoryFactory.get("categories");
        Product = this.$RepositoryFactory.get("products");
    },
    created() {
        if (this.$route.params.step) {
            this.step = !isNaN(this.$route.params.step)
                ? parseInt(this.$route.params.step)
                : 1;
        }
        // this.details = [...this.toReactiveDataFormat(this.newProduct.details)];
        // this.options = [
        //     ...this.toReactiveOptionsDataFormat(this.newProduct.options)
        // ];
        if (process.env.CLIENT) {
            this.fetchCategories();
        }
    },
    destroyed() {
        this.clearState();
    },
    mounted() {
        this.fetchNewProdSess();
    },
    computed: {
        hasImgs() {
            return this.selimgs && this.selimgs.length > 0;
        }
    },
    data() {
        return {
            loadingStep1: false,
            loadingStep2: false,
            loadingStep3: false,
            loadingStep4: false,
            step: 1,
            selimgs: [],
            selcolor: "",
            categories: [],
            colors: [],
            details: [
                // {
                //     group: "General",
                //     items: [
                //         { label: "Ingredients", value: "2 eggs, 1 cup flour" }
                //     ],
                //     edit: true
                // },
                // {
                //     group: "Shipping",
                //     items: [
                //         { label: "Weight", value: "1.5 kg" },
                //         { label: "Dimensions", value: "20cm x 15cm x 60cm" }
                //     ],
                //     edit: true
                // }
            ],
            options: [
                // {
                //     attribute: "Theme",
                //     choices: [
                //         { value: "Other", price: 10 },
                //         { value: "Disney Character", price: -10 },
                //         { value: "Cars", price: 0 }
                //     ],
                //     edit: false,
                //     userCustomizable: false
                // },
                // {
                //     attribute: "Icing type",
                //     choices: [
                //         { value: "Butter creme", price: 0 },
                //         { value: "Fondant", price: 50 }
                //     ],
                //     edit: false,
                //     userCustomizable: true
                // }
            ],
            newProduct: {
                id: 0,
                name: "",
                category: null,
                basePrice: 100,
                minOrderQuantity: 1,
                description: "",
                colors: [],
                options: [
                    // {
                    //     attribute: "Theme",
                    //     choices: [
                    //         { value: "Other", price: 110 },
                    //         { value: "Disney Character", price: -10 },
                    //         { value: "Cars", price: 0 }
                    //     ],
                    //     userCustomizable: true
                    // },
                    // {
                    //     attribute: "Icing type",
                    //     choices: [
                    //         { value: "Other", price: 23 },
                    //         { value: "Butter creme", price: 0 },
                    //         { value: "Fondant", price: 50 }
                    //     ],
                    //     userCustomizable: false
                    // }
                ],
                details: [
                    // {
                    //     group: "General",
                    //     items: {
                    //         Ingredients: "2 eggs, 2 tbsp vanilla, etc.",
                    //         Sweetness: 123
                    //     }
                    // },
                    // {
                    //     group: "Shipping",
                    //     items: {
                    //         Weight: "1.5 kg",
                    //         Dimensions: "20cm x 15cm x 60cm"
                    //     }
                    // }
                ],
                images: []
            }
        };
    },
    methods: {
        _isValidCategory(val) {
            if (
                !(
                    this.newProduct.category &&
                    this.categories &&
                    this.categories.length > 0
                )
            ) {
                return "Invalid category selected";
            }

            const categoryItem = this.categories.find(option => {
                return option.value === this.newProduct.category;
            });

            if (!categoryItem) return "Invalid category selected";

            return true;
        },

        _isUniqueDetailGrp(val) {
            if (this.details && this.details.length > 0) {
                const res = this.details.filter(item => {
                    return item.group == val;
                });
                if (res.length > 1) {
                    return "Duplicate detail group title.";
                }
            }
            return true;
        },

        _isUniqueOptionGrp(val) {
            if (this.options && this.options.length > 0) {
                const res = this.options.filter(item => {
                    return item.attribute == val;
                });
                if (res.length > 1) {
                    return "Duplicate option group title.";
                }
            }
            return true;
        },

        _isValidColor(val) {
            if (val && val.length > 0) {
                const hexpattern = /^#([0-9A-F]{3}){1,2}$/i;
                for (let i in val) {
                    if (!hexpattern.test(val[i]))
                        return "Invalid hex color selected";
                }
            }

            return true;
        },

        _validImage(files) {
            const filtered = files.filter(
                file =>
                    file.size < 1024 * 1024 * 2 && // 2MB
                    ["image/png", "image/jpeg"].includes(file.type)
            );

            if (
                !filtered ||
                filtered.length < 1 ||
                filtered.length < files.length
            ) {
                this.showNotif(false, "Limit image size to 2MB (PNG/JPG).");
            }

            return filtered;
        },

        async fetchCategories() {
            try {
                const resp = await Category.getProductCategorySelection();
                this.categories = resp.slice();
            } catch (err) {
                this.showNotif(false, "Could not retrieve product categories.");
            }
        },

        async fetchNewProdSess() {
            try {
                if (this.isOngoing()) {
                    // Fetch product details (Ongoing)
                    this.newProduct = JSON.parse(
                        JSON.stringify(this.getProduct())
                    );
                    if (this.newProduct.details) {
                        this.details = [
                            ...this.toReactiveDataFormat(
                                this.newProduct.details
                            )
                        ];
                    }
                    if (this.newProduct.options) {
                        this.options = [
                            ...this.toReactiveOptionsDataFormat(
                                this.newProduct.options
                            )
                        ];
                    }
                }
            } catch (err) {
                this.showNotif(false, "Could not retrieve product details.");
            }
        },

        toJSONFormatOptions: function(options) {
            const retArr = [];

            if (options && options.length > 0) {
                options.map(option => {
                    let choices = [];

                    // copy choices list
                    for (let i in option.choices) {
                        choices.push({
                            value: option.choices[i].value,
                            price: option.choices[i].price
                        });
                    }

                    // check settings
                    if (
                        option.userCustomizable &&
                        !this.hasCustomChoice(option.choices)
                    ) {
                        // add 'Other'
                        choices.unshift({ value: "Other", price: 0 });
                    } else if (
                        !option.userCustomizable &&
                        this.hasCustomChoice(option.choices)
                    ) {
                        // remove 'Other'
                        choices = this.filteredOptions(choices, false);
                    }

                    retArr.push({
                        attribute: option.attribute,
                        choices: choices,
                        userCustomizable: option.userCustomizable
                    });
                });
            }
            return retArr;
        },
        toReactiveOptionsDataFormat: function(options) {
            const retArr = [];
            if (options && options.length > 0) {
                options.map(option => {
                    retArr.push({
                        attribute: option.attribute,
                        userCustomizable: option.userCustomizable,
                        choices: option.choices,
                        edit: true
                    });
                });
            }

            return retArr;
        },
        toJSONFormatDetails: function(details) {
            const retArr = [];

            if (details && details.length > 0) {
                details.map(detail => {
                    const groupItems = {};
                    if (detail.items && detail.items.length > 0) {
                        detail.items.map(item => {
                            groupItems[item.label] = item.value;
                        });
                    }

                    if (detail.group && Object.keys(groupItems).length > 0) {
                        retArr.push({
                            group: detail.group,
                            items: groupItems
                        });
                    }
                });
            }

            return retArr;
        },
        toReactiveDataFormat: function(details) {
            const retArr = [];

            if (details && details.length > 0) {
                details.map(detail => {
                    const groupItemsArr = [];
                    if (detail.items && Object.keys(detail.items).length > 0) {
                        for (let key in detail.items) {
                            groupItemsArr.push({
                                label: key,
                                value: detail.items[key].toString()
                            });
                        }
                    }
                    if (detail.group && groupItemsArr.length > 0) {
                        retArr.push({
                            group: detail.group,
                            items: groupItemsArr,
                            edit: true
                        });
                    }
                });
            }

            // console.log(retArr);
            return retArr;
        },
        filteredOptions(list, showOther) {
            if (list && list.length > 0 && !showOther) {
                return list.filter(item => {
                    return item.value !== "Other";
                });
            }
            return list;
        },
        saveStep1: function(evt) {
            this.loadingStep1 = true;
            this.$refs.step1Form
                .validate()
                .then(async success => {
                    if (success) {
                        let res = {};
                        this.newProduct.details = [
                            ...this.toJSONFormatDetails(this.details)
                        ];
                        if (this.isOngoing()) {
                            // Update product and return id (inactive)
                            res = await Product.updateProduct(
                                this.newProduct.id,
                                this.newProduct
                            );
                        } else {
                            // Create product and return id (inactive)
                            res = await Product.createProduct(this.newProduct);
                            this.newProduct.id = res.id;
                        }
                        // Save to localstorage
                        this.setProductInfo(
                            JSON.parse(JSON.stringify(this.newProduct))
                        );
                        this.loadingStep1 = false;
                        this.step = 2;
                        this.$router
                            .replace("/products/add/2")
                            .catch(err => {});
                    }
                })
                .catch(err => {
                    this.showNotif(false, "Could not create product.");
                    this.loadingStep1 = false;
                });
        },
        saveStep2: function(imgs) {
            this.loadingStep2 = true;
            this.$refs.step2Form
                .validate()
                .then(async success => {
                    if (success) {
                        if (imgs) {
                            this.newProduct.images = imgs.slice();
                            this.setProductImages(imgs);
                        }
                        this.loadingStep2 = false;
                        this.step = 3;
                        this.$router
                            .replace("/products/add/3")
                            .catch(err => {});
                    }
                })
                .catch(err => {
                    this.showNotif(false, "Error has occurred.");
                    this.loadingStep2 = false;
                });
        },
        saveStep3: function(evt) {
            this.loadingStep3 = true;
            this.$refs.step3Form
                .validate()
                .then(async success => {
                    if (success) {
                        if (!this.isOngoing()) {
                            throw "Start from step 1.";
                        }
                        const opts = [
                            ...this.toJSONFormatOptions(this.options)
                        ];
                        this.newProduct.options = opts.slice();
                        const res = await Product.updateProduct(
                            this.newProduct.id,
                            { options: this.newProduct.options }
                        );
                        this.setProductOptions(opts);
                        this.loadingStep3 = false;
                        this.step = 4;
                        this.$router
                            .replace("/products/add/4")
                            .catch(err => {});
                    }
                })
                .catch(err => {
                    this.showNotif(
                        false,
                        "Error adding customization options."
                    );
                    this.loadingStep3 = false;
                });
        },
        saveStep4: function(evt) {
            this.loadingStep4 = true;
            this.$refs.step4Form
                .validate()
                .then(async success => {
                    if (success) {
                        if (!this.isOngoing()) {
                            throw "Start from step 1.";
                        }
                        const res = await Product.updateProduct(
                            this.newProduct.id,
                            { isActive: true }
                        );
                        this.loadingStep4 = false;
                        this.returnToPageIndex("/products");
                    }
                })
                .catch(err => {
                    this.showNotif(false, "Could not activate product.");
                    this.loadingStep4 = false;
                });
        },
        // goBack: function(step) {
        //     if ([1, 2, 3].includes(step)) {
        //         this.step = step;
        //         this.$router.replace("/products/add/" + step).catch(err => {});
        //     }
        // },
        removeGroup: function(key) {
            this.$delete(this.details, key);
        },
        removeDetailItem: function(grp, key) {
            this.$delete(this.details[grp].items, key);
            if (this.details[grp].items.length === 0) {
                this.removeGroup(grp);
            }
        },
        appendGroup: function(evt) {
            this.details.push({
                group: "New Group",
                edit: true,
                items: [{ label: "", value: "" }]
            });
        },
        appendDetailItem: function(grp) {
            if (!isNaN(grp) && this.details[grp]) {
                this.details[grp].items.push({ label: "", value: "" });
            }
        },

        removeOptions: function(key) {
            this.$delete(this.options, key);
        },
        removeOptionItem: function(grp, key, value) {
            this.$delete(this.options[grp].choices, key);
            if (this.options[grp].choices.length === 0) {
                this.removeOptions(grp);
            } else if (value == "Other") {
                this.options[grp].userCustomizable = false;
            }
        },
        appendOptions: function(evt) {
            this.options.push({
                attribute: "New Option",
                userCustomizable: false,
                edit: true,
                choices: [{ value: "", price: "" }]
            });
        },
        appendOptionItem: function(grp) {
            if (!isNaN(grp) && this.options[grp]) {
                this.options[grp].choices.push({ value: "", price: "" });
            }
        },
        hasCustomChoice(list) {
            if (list && list.length > 0) {
                return list.find(item => {
                    return item.value == "Other";
                });
            }
            return false;
        },
        toggleCustomChoice: function(toggle, grp) {
            if (toggle) {
                if (
                    this.options[grp] &&
                    !this.hasCustomChoice(this.options[grp].choices)
                ) {
                    this.options[grp].choices.unshift({
                        value: "Other",
                        price: ""
                    });
                }
            } else {
                if (
                    this.options[grp] &&
                    this.hasCustomChoice(this.options[grp].choices)
                ) {
                    this.options[grp].choices.shift();
                }
            }
        },
        async startUpload() {
            try {
                this.loadingStep2 = true;
                if (!this.isOngoing()) {
                    throw "Start from step 1.";
                }
                if (this.hasImgs) {
                    let fd = new FormData();
                    this.selimgs.forEach(file => {
                        fd.append("image", file);
                    });
                    fd.append("imageType", "gallery");
                    const res = await Product.uploadImgs(
                        this.newProduct.id,
                        fd
                    );
                    // clear
                    this.selimgs = [];
                    this.saveStep2(res);
                } else {
                    this.saveStep2(false);
                }
            } catch (err) {
                this.showNotif(false, "Could not upload images.");
                this.loadingStep2 = false;
            }
        },
        imgsRemoved(file) {
            // Remove from list
            this.selimgs = this.selimgs.filter(item => item !== file[0]);
        },
        imgsAdded(file) {
            // Append to list
            this.selimgs.push(...file);
        },
        onImgUploadFailed: function(info) {
            this.showNotif(false, "Failed to add your selected image. ");
        }
    }
};

