            const d = new Date();
            return new Date(d.getFullYear(), d.getMonth() + 1, 0);
        }
    },
    methods: {
        // Shared methods
        replaceAll: function(txt, search, replace) {
            return txt.replace(new RegExp(search, "g"), replace);
