import ConfirmDialog from "../../components/ConfirmDialog";
import HelperMixin from "../../mixins/helpers";
let Comment = null;

export default {
    name: "CommentIndex",
    components: { ConfirmDialog },
    mixins: [HelperMixin],

    meta() {
        return {
            title: "Product Comments"
        };
    },
    beforeCreate() {
        Comment = this.$RepositoryFactory.get("comments");
    },
    created() {
        if (this.$route.query.s) {
            this.search = this.$route.query.s;
        }
        if (this.$route.query.p && !isNaN(this.$route.query.p)) {
            this.pagination.page = Number.parseInt(this.$route.query.p);
        }
    },
    mounted() {
        this.onRequest({
            pagination: this.pagination,
            filter: undefined
        });
    },
    data() {
        return {
            announcementList: [],
            search: "",
            searchReq: null,
            loading: false,
            showDlg: false,
            toFlagID: -1,
            targetFlagVal: false,
            pagination: {
                sortBy: "posted",
                descending: true,
                page: 1,
                rowsPerPage: 9
            },
            columns: [
                {
                    name: "text",
                    field: "text",
                    label: "Text",
                    align: "left",
                    required: true,
                    sortable: true
                },
                {
                    name: "posted",
                    field: "posted",
                    align: "left",
                    label: "Posted",
                    sortable: true
                },
                {
                    name: "product",
                    field: "product",
                    align: "left",
                    label: "Product",
                    sortable: true
                },
                {
                    name: "author",
                    field: "author",
                    align: "left",
                    label: "Author",
                    sortable: true
                }
            ],
            data: [],
            original: []
        };
    },
    methods: {
        searchClear(evt) {
            let query = Object.assign({}, this.$route.query);
            delete query.s;
            this.$router.replace({ query }).catch(err => {});
            this.search = "";
        },
        searchInput(val) {
            let searchQry = Object.assign({}, this.$route.query, { s: val });
            if (!val) delete searchQry.s;

            this.$router
                .replace({
                    query: searchQry
                })
                .catch(err => {});
        },
        onChgPage(newPg) {
            let pageQry = Object.assign({}, this.$route.query, {
                p: newPg.page
            });
            if (!newPg) delete pageQry.p;

            this.$router
                .replace({
                    query: pageQry
                })
                .catch(err => {});
        },
        async onRequest(props) {
            this.loading = true;
            try {
                const resp = await Comment.getComments();
                this.original = resp;
                this.data = this.original.slice();
            } catch (err) {
                this.showNotif(false, "Could not retrieve comments. ");
            } finally {
                this.loading = false;
            }
        },
        confirmAction(commentID, bool) {
            this.targetFlagVal = bool;
            this.showDlg = true;
            this.toFlagID = commentID;
        },

        async onProceed() {
            if (this.toFlagID !== -1) {
                try {
                    // Toggle flag comment
                    const resp = await Comment.flagComment(
                        this.toFlagID,
                        this.targetFlagVal
                    );

                    this.showNotif(
                        true,
                        this.targetFlagVal
                            ? "Comment has been FLAGGED."
                            : "Comment has been UNFLAGGED."
                    );
                    // Update
                    this.data.forEach(el => {
                        if (el.id === this.toFlagID) {
                            el.flagged = this.targetFlagVal;
                        }
                    });
                    // Reset
                    this.toFlagID = -1;
                    this.targetFlagVal = false;
                } catch (err) {
                    this.showNotif(false, "Could not update item. ");
                }
            }
        }
    }
};

