import ConfirmDialog from "../../components/ConfirmDialog";
import HelperMixin from "../../mixins/helpers";
let Discount = null;

export default {
    name: "DiscountsIndex",
    components: { ConfirmDialog },
    mixins: [HelperMixin],

    meta() {
        return {
            title: "Product Discounts"
        };
    },
    beforeCreate() {
        Discount = this.$RepositoryFactory.get("discounts");
    },
    created() {
        if (this.$route.query.s) {
            this.search = this.$route.query.s;
        }
        if (this.$route.query.p && !isNaN(this.$route.query.p)) {
            this.pagination.page = Number.parseInt(this.$route.query.p);
        }
    },
    mounted() {
        this.onRequest({
            pagination: this.pagination,
            filter: undefined
        });
    },
    data() {
        return {
            discountList: [],
            search: "",
            searchReq: null,
            loading: false,
            showDlg: false,
            toDelID: -1,
            pagination: {
                sortBy: "start",
                descending: true,
                page: 1,
                rowsPerPage: 9
            },
            columns: [
                {
                    name: "percent",
                    field: "percent",
                    label: "Discount %",
                    align: "left",
                    required: true,
                    sortable: true
                },
                {
                    name: "target",
                    field: "target",
                    align: "left",
                    label: "Target Customer",
                    sortable: true
                },
                {
                    name: "start",
                    field: "start",
                    align: "left",
                    label: "Start",
                    sortable: true
                },
                {
                    name: "end",
                    field: "end",
                    align: "left",
                    label: "End",
                    sortable: true
                },
                {
                    name: "count",
                    field: "count",
                    align: "left",
                    label: "No. of Products",
                    sortable: true
                },
                {
                    name: "actions",
                    align: "left",
                    label: "Actions"
                }
            ],
            data: [],
            original: []
        };
    },
    methods: {
        searchClear(evt) {
            let query = Object.assign({}, this.$route.query);
            delete query.s;
            this.$router.replace({ query }).catch(err => {});
            this.search = "";
        },
        searchInput(val) {
            let searchQry = Object.assign({}, this.$route.query, { s: val });
            if (!val) delete searchQry.s;

            this.$router
                .replace({
                    query: searchQry
                })
                .catch(err => {});
        },
        onChgPage(newPg) {
            let pageQry = Object.assign({}, this.$route.query, {
                p: newPg.page
            });
            if (!newPg) delete pageQry.p;

            this.$router
                .replace({
                    query: pageQry
                })
                .catch(err => {});
        },
        async onRequest(props) {
            this.loading = true;
            try {
                const resp = await Discount.getAllDiscounts();
                this.original = resp;
                this.data = this.original.slice();
            } catch (err) {
                this.showNotif(false, "Could not retrieve discounts. ");
            } finally {
                this.loading = false;
            }
        },

        async onRemove() {
            if (this.toDelID !== -1) {
                try {
                    const resp = await Discount.deleteDiscount(this.toDelID);
                    this.showNotif(
                        true,
                        "Successfully removed discount entry."
                    );

                    // Remove from list and Reset
                    this.$delete(
                        this.data,
                        this.data.findIndex(el => el.id == this.toDelID)
                    );
                    this.toDelID = -1;
                } catch (err) {
                    this.showNotif(false, "Could not delete item. ");
                }
            }
        },

        confirmDel(psaID) {
            this.showDlg = true;
            this.toDelID = psaID;
        }
    }
};

